import React from 'react'

// styled-components imports
import styled from 'styled-components';
import Theme from '../theme';

// styled-components
const Styledp = styled.p `
    font-size: 1em;
    font-family: $para;
    margin: 0;
    font-weight: 600;
    font-style: italic;
`
const StyledAwardDiv = styled.div `
    width: 100%;
    margin: .5em 0;
    text-align: center;

    hr {
        width: 50%;
    }
`

export default function Award({ award }) {
    return (
        <StyledAwardDiv>
            <p className="f-p f-b f-i">{award}</p>
            <hr/>
        </StyledAwardDiv>
    )
}
